CREATE TABLE `letter`.`letter` ( `id` INT(10) NOT NULL , `nickname` VARCHAR(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '昵称' , `name` VARCHAR(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '姓名' , `idcard` VARCHAR(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '身份证' , `phone` VARCHAR(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '电话' , `email` VARCHAR(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '邮箱' , `career` VARCHAR(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '职业' , `address` VARCHAR(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '地址' , `title` VARCHAR(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '标题' , `content` TEXT CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '正文' , `time` INT(10) UNSIGNED NOT NULL COMMENT '时间' , PRIMARY KEY (`id`)) ENGINE = InnoDB;
ALTER TABLE `letter` CHANGE `id` `id` INT(10) NOT NULL AUTO_INCREMENT;